mapper.select:
SELECT cp.id, cp.title, cp.offer_resource AS "offerResource", u.id AS "user.id", u.position AS "user.position", u.avatar AS "user.avatar", u.name AS "user.name", cp.views, cp.create_date AS "createDate", cp.status, c.name AS "company.name", cp.area FROM w_cooperation cp JOIN w_user u ON cp.user_id = u.id JOIN w_company c ON cp.company_id = c.id
cp.del_flag = #{DEL_FLAG_NORMAL}
AND cp.status=#{status}
AND u.name like CONCAT('%',#{user.name},'%')
AND cp.name like CONCAT('%',#{company.name},'%')
AND cp.create_date >= #{beginDate}
AND cp.create_date <= #{endDate}
ORDER BY cp.create_date DESC

SELECT id, photo
FROM w_adver
where id = 1;

mapper.update:
update w_adver
set photo = #{photo,jdbcType=VARCHAR}
where id = 1;

UPDATE w_cooperation
SET status = #{status}
WHERE id = #{id}

UPDATE w_cooperation
SET del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

update w_cooperation
set views=views+1
where id=#{id}
