mapper.select:
select

from w_user u
where u.id = #{id,jdbcType=VARCHAR}

SELECT u.id, u.openid, u.name, u.position, u.companion, u.wechat_account AS "wechatAccount", u.vip_level AS "vipLevel", u.mobile, u.company_id AS "company.id", u.create_date AS "createDate", u.avatar, cp.name AS "company.name", u.adv_count AS "advCount", u.ph_count AS "phCount", u.balance, u.companion FROM w_user u LEFT JOIN w_company cp ON cp.id=u.company_id
1=1
AND u.openid=#{openid}
AND u.id=#{id};

SELECT u.id, u.name, u.position, u.vip_level AS "vipLevel", u.mobile, c.name AS "company.name", u.company_id AS "company.id", u.create_date AS "createDate" FROM w_user u JOIN w_company c ON u.company_id = c.id
u.del_flag=#{DEL_FLAG_NORMAL}
AND u.id=#{id}
AND u.name like CONCAT('%',#{name},'%')
AND c.name like CONCAT('%',#{company.name},'%')
ORDER BY u.create_date DESC

mapper.update:
UPDATE w_user
SET vip_level = #{vipLevel}
WHERE id = #{id}

update w_user
set adv_count=adv_count-1
where id=#{id}
