mapper.select:
select

from w_user u
where u.id = #{id,jdbcType=VARCHAR}

SELECT u.id, u.name, u.position, u.vip_level AS "vipLevel", u.mobile, c.name AS "company.name", u.company_id AS "company.id", u.create_date AS "createDate" FROM w_user u JOIN w_company c ON u.company_id = c.id
u.del_flag=#{DEL_FLAG_NORMAL}
AND u.id=#{id}
AND u.name like CONCAT('%',#{name},'%')
AND c.name like CONCAT('%',#{company.name},'%')
ORDER BY u.create_date DESC

mapper.update:
UPDATE w_user
SET vip_level = #{vipLevel}
WHERE id = #{id}
